/**
 * Adds CSS styles to the document head.
 * @param {string} stylesString - The CSS styles as a string.
 * @returns {jQuery} The jQuery object for chaining.
 * @author dfproffesional
 * @see https://github.com/dfproffesional/jquery-plugins
 */
(function($){$.fn.addStylesToHead=function(stylesString){return this.each(function(){if(stylesString){const blob=new Blob([stylesString],{type:"text/css"});const url=URL.createObjectURL(blob);const link=$("<link>",{rel:"stylesheet",type:"text/css",href:url});$("head").append(link)}else{console.warn("No styles content provided.")}})}})(jQuery);
